import Vue from 'vue';
import Vuex from 'vuex';
import Swal from 'sweetalert2';
import axios from 'axios';  // Asegúrate de importar axios

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    email: '',
    token: null,
  },
  mutations: {
    login(state, { email, token }) {
      state.email = email;
      state.token = token;
      localStorage.setItem('email', email);
      localStorage.setItem('token', token);
      Swal.fire({
        position: 'top-end',
        icon: 'success',
        title: 'Sesión iniciada',
        showConfirmButton: false,
        timer: 1500,
      });
    },
    logout(state) {
      state.email = '';
      state.token = null;
      localStorage.removeItem('email');
      localStorage.removeItem('token');
      Swal.fire({
        position: 'top-end',
        icon: 'success',
        title: 'Hasta luego',
        showConfirmButton: false,
        timer: 1500,
      });
    },
    initializeStore(state) {
      if (localStorage.getItem('email')) {
        state.email = localStorage.getItem('email');
        state.token = localStorage.getItem('token');
      }
    },
  },
  actions: {
    async login({ commit }, { nombreUsuario, contraseña }) {
      try {
        const response = await axios.post('/api/usuarios/login', { nombreUsuario, contraseña });

        if (response.data.success) {
          const token = response.data.token;

          // Guarda el token en localStorage
          localStorage.setItem('token', token);

          // Agrega el token a las solicitudes de Axios como encabezado de autorización
          axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

          // Llama a la mutación para actualizar el estado con el email y muestra la alerta
          commit('login', { email: response.data.email, token });
        } else {
          // Muestra una alerta en caso de fallo
          Swal.fire({
            position: 'top-end',
            icon: 'error',
            title: 'Inicio de sesión fallido',
            showConfirmButton: false,
            timer: 1500,
          });
        }
      } catch (error) {
        // Muestra una alerta en caso de error en la solicitud
        console.error('Error al iniciar sesión:', error);
        Swal.fire({
          position: 'top-end',
          icon: 'error',
          title: 'Error al iniciar sesión',
          showConfirmButton: false,
          timer: 1500,
        });
      }
    },
  },
  modules: {},
});
