import Vue from 'vue'
import Vuex from 'vuex'
import Swal from 'sweetalert2'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    email: ""
  },
  getters: {
  },
  mutations: {

    login(state, email) {
      state.email = email;
      localStorage.setItem('email', email);

      // Muestra la alerta
      Swal.fire({
        position: "top-end",
        icon: "success",
        title: "Sesión iniciada",
        showConfirmButton: false,
        timer: 1500
      });
    },
    logout(state) {
      state.email =""
      localStorage.removeItem('email')
      Swal.fire({
        position: "top-end",
        icon: "success",
        title: "Hasta luego",
        showConfirmButton: false,
        timer: 1500
      });
    },
    initializeStore(state) {
      // Inicializa el estado con el email almacenado en localStorage
      if (localStorage.getItem('email')) {
        state.email = localStorage.getItem('email');
      }

      // Inicializa los encabezados de autorización de Axios si hay un token almacenado
      const token = localStorage.getItem('token');
      if (token) {
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      }}
  },
  actions: {
    async login({ commit }, { nombreUsuario, contraseña }) {
      try {
        const response = await axios.post('/api/usuarios/login', { nombreUsuario, contraseña });

        if (response.data.success) {
          const token = response.data.token;

          // Guarda el token en localStorage
          localStorage.setItem('token', token);

          // Agrega el token a las solicitudes de Axios como encabezado de autorización
          axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

          // Llama a la mutación para actualizar el estado con el email y muestra la alerta
          commit('login', response.data.email);
        } else {
          // Muestra una alerta en caso de fallo
          Swal.fire({
            position: "top-end",
            icon: "error",
            title: "Inicio de sesión fallido",
            showConfirmButton: false,
            timer: 1500
          });
        }
      } catch (error) {
        // Muestra una alerta en caso de error en la solicitud
        console.error('Error al iniciar sesión:', error);
        Swal.fire({
          position: "top-end",
          icon: "error",
          title: "Error al iniciar sesión",
          showConfirmButton: false,
          timer: 1500
        });
      }
    },
  },
  modules: {
  }
})
