import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    email: ""
  },
  getters: {
  },
  mutations: {

    login(state, email) {
      state.email = email
      localStorage.setItem('email', email);

    }
  },
  actions: {
  },
  modules: {
  }
})
