import Vue from 'vue'
import Vuex from 'vuex'
import Swal from 'sweetalert2'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    token: ""
  },
  getters: {
  },
  mutations: {

  },
  actions: {
    async login({ commit }, { nombreUsuario, contraseña }) {
      try {
        const response = await axios.post('/api/usuarios/login', { nombreUsuario, contraseña });

        if (response.data.success) {
          const token = response.data.token;
          console.log(token);
          // Guarda el token en localStorage
          localStorage.setItem('token', token);

          // Agrega el token a las solicitudes de Axios como encabezado de autorización
          axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

          // Llama a la mutación para actualizar el estado con el email y muestra la alerta
          commit('login', response.data.token);
        } else {
          // Muestra una alerta en caso de fallo
          Swal.fire({
            position: "top-end",
            icon: "error",
            title: "Inicio de sesión fallido",
            showConfirmButton: false,
            timer: 1500
          });
        }
      } catch (error) {
        // Muestra una alerta en caso de error en la solicitud
        console.error('Error al iniciar sesión:', error);
        Swal.fire({
          position: "top-end",
          icon: "error",
          title: "Error al iniciar sesión",
          showConfirmButton: false,
          timer: 1500
        });
      }
    },
  },
  modules: {
  }
})
